import Ffmpeg from "fluent-ffmpeg";
import fs from 'fs';
import path from "path";

const input_directory = path.resolve('./input');
const output_directory = path.resolve('./output');

// Create directories if they don't exist
if (!fs.existsSync(input_directory)) {
  fs.mkdirSync(input_directory);
}
if (!fs.existsSync(output_directory)) {
  fs.mkdirSync(output_directory);
}

const youtubeVideoURLs = [
  'https://www.youtube.com/watch?v=G7KNmW9a75Y',
  'https://www.youtube.com/watch?v=6ZUIwj3FgUY',
  'https://www.youtube.com/watch?v=ArmDp-zijuc',
  'https://www.youtube.com/watch?v=XoiOOiuH8iI',
];

async function downloadAndProcessVideos() {
  const videoPromises = youtubeVideoURLs.map(async (url, index) => {
    const videoInfo = await ytdl.getInfo(url);
    const videoStream = await ytdl(url, { filter: 'audioandvideo' })
      .pipe(fs.createWriteStream(`${input_directory}/video${index}.mp4`));

    return new Promise(resolve => {
      videoStream.on('finish', ()=> {
        console.warn('finished');
        resolve({videoStream, videoInfo});
      })
    })

  });

  const videoInfoArray = await Promise.all(videoPromises);

  const ffmpegCommand = Ffmpeg();
  ffmpegCommand.setFfmpegPath(path.resolve('/ffmpeg/bin/ffmpeg.exe'));
  ffmpegCommand.setFfprobePath(path.resolve('/ffmpeg/bin/ffprobe.exe'));

  videoInfoArray.forEach(({ videoStream }, index) => {
    ffmpegCommand.input(videoStream.path)
  });

  // Save the final video file.
  const filterGraph = [
    `nullsrc=size=640x480 [base];`,
    `[0:v] setpts=PTS-STARTPTS, scale=320x240 [upperleft];`,
    `[1:v] setpts=PTS-STARTPTS, scale=320x240 [upperright];`,
    `[2:v] setpts=PTS-STARTPTS, scale=320x240 [lowerleft];`,
    `[3:v] setpts=PTS-STARTPTS, scale=320x240 [lowerright];`,
    `[base][upperleft] overlay=shortest=1 [tmp1];`,
    `[tmp1][upperright] overlay=shortest=1:x=320[tmp2];`,
    `[tmp2][lowerleft] overlay=shortest=1:y=240 [tmp3];`,
    `[tmp3][lowerright] overlay=shortest=1:x=320:y=240[v];`,
    `[0:a][1:a]amerge[a1];`,
    `[a1][2:a]amerge[a2];`,
    `[a2][3:a]amerge[a]`,
  ].join(' ');

  ffmpegCommand
    .complexFilter(filterGraph)
    .outputOptions('-map', '[v]')
    .outputOptions('-map', '[a]')
    .output(`${output_directory}/final.mp4`)
    .on('end', () => console.log('Processing finished!'))
    .on('error', (err, stdout, stderr) => {
      console.error('Error:', err)
      console.log('ffmpeg stdout:', stdout);
      console.log('ffmpeg stderr:', stderr);
    })
    .run()
}

// Start video download and processing
downloadAndProcessVideos();
